#!/usr/bin/env bash
set -euo pipefail

ROOT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")/.." && pwd)"
IMAGE_NAME="${NEUROION_INSTALL_E2E_IMAGE:-${NEUROIONBOT_INSTALL_E2E_IMAGE:-neuroion-install-e2e:local}}"
INSTALL_URL="${NEUROION_INSTALL_URL:-${NEUROIONBOT_INSTALL_URL:-https://neuroion.bot/install.sh}}"

OPENAI_API_KEY="${OPENAI_API_KEY:-}"
ANTHROPIC_API_KEY="${ANTHROPIC_API_KEY:-}"
ANTHROPIC_API_TOKEN="${ANTHROPIC_API_TOKEN:-}"
NEUROION_E2E_MODELS="${NEUROION_E2E_MODELS:-${NEUROIONBOT_E2E_MODELS:-}}"

echo "==> Build image: $IMAGE_NAME"
docker build \
  -t "$IMAGE_NAME" \
  -f "$ROOT_DIR/scripts/docker/install-sh-e2e/Dockerfile" \
  "$ROOT_DIR/scripts/docker/install-sh-e2e"

echo "==> Run E2E installer test"
docker run --rm \
  -e NEUROION_INSTALL_URL="$INSTALL_URL" \
  -e NEUROION_INSTALL_TAG="${NEUROION_INSTALL_TAG:-${NEUROIONBOT_INSTALL_TAG:-latest}}" \
  -e NEUROION_E2E_MODELS="$NEUROION_E2E_MODELS" \
  -e NEUROION_INSTALL_E2E_PREVIOUS="${NEUROION_INSTALL_E2E_PREVIOUS:-${NEUROIONBOT_INSTALL_E2E_PREVIOUS:-}}" \
  -e NEUROION_INSTALL_E2E_SKIP_PREVIOUS="${NEUROION_INSTALL_E2E_SKIP_PREVIOUS:-${NEUROIONBOT_INSTALL_E2E_SKIP_PREVIOUS:-0}}" \
  -e OPENAI_API_KEY="$OPENAI_API_KEY" \
  -e ANTHROPIC_API_KEY="$ANTHROPIC_API_KEY" \
  -e ANTHROPIC_API_TOKEN="$ANTHROPIC_API_TOKEN" \
  "$IMAGE_NAME"
